import React, { useEffect, useState } from "react";
import axios from "axios";

const TabelaItems = () => {
  const [items, setItems] = useState([]);
  const [nome, setNome] = useState("");
  const [custo, setCusto] = useState("");
  const [dataLimite, setDataLimite] = useState("");

  // Função para obter dados da API
  const fetchItems = async () => {
    try {
      const response = await axios.get("https://listadetarefas-2.onrender.com");
      setItems(response.data);
    } catch (error) {
      console.error("Erro ao buscar os itens", error);
    }
  };

  // Função para adicionar um novo item
  const addItem = async (e) => {
    // Evitar o envio do formulário
    const ordemApresentacao =
      items.length > 0
        ? Math.max(...items.map((item) => item.ordemApresentacao)) + 1
        : 1; // Calcula a próxima ordem
    const newItem = {
      nome,
      custo: parseFloat(custo),
      dataLimite,
      ordemApresentacao,
    };

    try {
      await axios.post("http://localhost:8080/api/items", newItem);
      fetchItems(); // Atualizar a lista de itens
      // Limpar os campos após o envio
      setNome("");
      setCusto("");
      setDataLimite("");
    } catch (error) {
      console.error("Erro ao adicionar item", error);
    }
  };

  useEffect(() => {
    fetchItems(); // Chama a função ao montar o componente
  }, []);

  return (
    <div>
      <h1>Tabela de Items</h1>
      <form onSubmit={addItem}>
        <input
          type="text"
          placeholder="Nome"
          value={nome}
          onChange={(e) => setNome(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Custo"
          value={custo}
          onChange={(e) => setCusto(e.target.value)}
          required
        />
        <input
          type="date"
          placeholder="Data Limite"
          value={dataLimite}
          onChange={(e) => setDataLimite(e.target.value)}
          required
        />
        <button type="submit">Adicionar Item</button>
      </form>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nome</th>
            <th>Custo</th>
            <th>Data Limite</th>
            <th>Ordem de Apresentação</th>
          </tr>
        </thead>
        <tbody>
          {items.map((item) => (
            <tr key={item.id}>
              <td>{item.id}</td>
              <td>{item.nome}</td>
              <td>{item.custo}</td>
              <td>{item.dataLimite}</td>
              <td>{item.ordemApresentacao}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default TabelaItems;
