import React, { useEffect, useState } from "react";
import axios from "axios";
import { format } from "date-fns"; // Importando a função format
import Modal from "./Modal"; // Importa o Modal
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd"; // Importando componentes de drag and drop
import "./TabelaItems.css"; // Importe seu arquivo CSS

const TabelaItems = () => {
  const [items, setItems] = useState([]);
  const [nome, setNome] = useState("");
  const [custo, setCusto] = useState("");
  const [dataLimite, setDataLimite] = useState("");
  const [isEditing, setIsEditing] = useState(null); // Estado para gerenciar o item que está sendo editado
  const [isModalOpen, setIsModalOpen] = useState(false); // Estado para controlar a abertura do modal

  const fetchItems = async () => {
    try {
      const response = await axios.get(
        "https://listadetarefas-2.onrender.com/tarefas"
      );
      setItems(response.data);
    } catch (error) {
      console.error("Erro ao buscar os itens", error);
    }
  };

  const addItem = async (e) => {
    e.preventDefault(); // Adicione isso para evitar o comportamento padrão do formulário

    const ordemApresentacao =
      items.length > 0
        ? Math.max(...items.map((item) => item.ordemApresentacao)) + 1
        : 1;

    const formattedDataLimite = format(new Date(dataLimite), "dd-MM-yyyy");

    const newItem = {
      nome,
      custo: parseFloat(custo),
      dataLimite: formattedDataLimite,
      ordemApresentacao,
    };

    try {
      const response = await axios.post(
        "https://listadetarefas-2.onrender.com/tarefas",
        newItem
      );
      fetchItems();
      setNome("");
      setCusto("");
      setDataLimite("");
    } catch (error) {
      console.error(
        "Erro ao adicionar item",
        error.response ? error.response.data : error.message
      );
    }
  };

  const deleteItem = async (id) => {
    const confirmDelete = window.confirm(
      "Você tem certeza que deseja excluir este item?"
    );
    if (confirmDelete) {
      try {
        await axios.delete(
          `https://listadetarefas-2.onrender.com/tarefas/${id}`
        );
        fetchItems(); // Recarrega os itens após a exclusão
      } catch (error) {
        console.error("Erro ao excluir o item", error);
      }
    }
  };

  const startEditing = (item) => {
    setIsEditing(item);
    setNome(item.nome);
    setCusto(item.custo);
    setDataLimite(item.dataLimite);
    setIsModalOpen(true);
  };

  const saveEdit = async (e) => {
    e.preventDefault();

    const nameExists = items.some(
      (item) => item.nome === nome && item.id !== isEditing.id
    );

    if (nameExists) {
      alert("O nome da tarefa já existe. Por favor, escolha outro nome.");
      return;
    }

    const updatedItem = {
      nome,
      custo: parseFloat(custo),
      dataLimite,
    };

    try {
      await axios.put(
        `https://listadetarefas-2.onrender.com/tarefas/${isEditing.id}`,
        updatedItem
      );
      fetchItems();
      setIsEditing(null);
      setIsModalOpen(false);
      setNome("");
      setCusto("");
      setDataLimite("");
    } catch (error) {
      console.error("Erro ao editar item", error);
    }
  };

  const handleOnDragEnd = (result) => {
    if (!result.destination) return;

    const reorderedItems = Array.from(items);
    const [removed] = reorderedItems.splice(result.source.index, 1);
    reorderedItems.splice(result.destination.index, 0, removed);

    setItems(reorderedItems);
  };

  useEffect(() => {
    fetchItems();
  }, []);

  return (
    <div>
      <h1>Tabela de Items</h1>
      <form onSubmit={addItem}>
        <input
          type="text"
          placeholder="Nome"
          value={nome}
          onChange={(e) => setNome(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Custo"
          value={custo}
          onChange={(e) => setCusto(e.target.value)}
          required
        />
        <input
          type="date"
          placeholder="Data Limite"
          value={dataLimite}
          onChange={(e) => setDataLimite(e.target.value)}
          required
        />
        <button type="submit">Adicionar Item</button>
      </form>

      <DragDropContext onDragEnd={handleOnDragEnd}>
        <Droppable droppableId="droppable">
          {(provided) => (
            <table {...provided.droppableProps} ref={provided.innerRef}>
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Nome</th>
                  <th>Custo</th>
                  <th>Data Limite</th>
                  <th>Ordem de Apresentação</th>
                  <th>Ações</th>
                </tr>
              </thead>
              <tbody>
                {items.map((item, index) => (
                  <Draggable key={item.id} draggableId={item.id} index={index}>
                    {(provided) => (
                      <tr
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        className={item.custo >= 1000 ? "highlight" : ""}
                      >
                        <td>{item.id}</td>
                        <td>{item.nome}</td>
                        <td>{item.custo}</td>
                        <td>{item.dataLimite}</td>
                        <td>{item.ordemApresentacao}</td>
                        <td>
                          <button onClick={() => startEditing(item)}>
                            Editar
                          </button>
                          <button onClick={() => deleteItem(item.id)}>
                            Excluir
                          </button>
                        </td>
                      </tr>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </tbody>
            </table>
          )}
        </Droppable>
      </DragDropContext>

      <Modal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)}>
        <h2>Editar Item</h2>
        <form onSubmit={saveEdit}>
          <input
            type="text"
            value={nome}
            onChange={(e) => setNome(e.target.value)}
            required
            placeholder="Nome"
          />
          <input
            type="number"
            value={custo}
            onChange={(e) => setCusto(e.target.value)}
            required
            placeholder="Custo"
          />
          <input
            type="date"
            value={dataLimite}
            onChange={(e) => setDataLimite(e.target.value)}
            required
            placeholder="Data Limite"
          />
          <button type="submit">Salvar</button>
        </form>
      </Modal>
    </div>
  );
};

export default TabelaItems;
