import React, { useEffect, useState } from "react";
import axios from "axios";
import { format } from "date-fns"; // Importando a função format
import "./TabelaItems.css"; // Importe seu arquivo CSS

const TabelaItems = () => {
  const [items, setItems] = useState([]);
  const [nome, setNome] = useState("");
  const [custo, setCusto] = useState("");
  const [dataLimite, setDataLimite] = useState("");
  const [isEditing, setIsEditing] = useState(null); // Estado para gerenciar o item que está sendo editado

  const fetchItems = async () => {
    try {
      const response = await axios.get(
        "https://listadetarefas-2.onrender.com/tarefas"
      );
      setItems(response.data);
    } catch (error) {
      console.error("Erro ao buscar os itens", error);
    }
  };

  const addItem = async (e) => {
    e.preventDefault(); // Adicione isso para evitar o comportamento padrão do formulário

    const ordemApresentacao =
      items.length > 0
        ? Math.max(...items.map((item) => item.ordemApresentacao)) + 1
        : 1;

    // Formata a dataLimite para o formato dd-MM-yyyy
    const formattedDataLimite = format(new Date(dataLimite), "dd-MM-yyyy");

    const newItem = {
      nome,
      custo: parseFloat(custo),
      dataLimite: formattedDataLimite,
      ordemApresentacao,
    };

    try {
      const response = await axios.post(
        "https://listadetarefas-2.onrender.com/tarefas",
        newItem
      );
      fetchItems();
      setNome("");
      setCusto("");
      setDataLimite("");
    } catch (error) {
      console.error(
        "Erro ao adicionar item",
        error.response ? error.response.data : error.message
      );
    }
  };

  const deleteItem = async (id) => {
    const confirmDelete = window.confirm(
      "Você tem certeza que deseja excluir este item?"
    );
    if (confirmDelete) {
      try {
        await axios.delete(
          `https://listadetarefas-2.onrender.com/tarefas/${id}`
        );
        fetchItems(); // Recarrega os itens após a exclusão
      } catch (error) {
        console.error("Erro ao excluir o item", error);
      }
    }
  };

  const startEditing = (item) => {
    setIsEditing(item); // Define o item que está sendo editado
    setNome(item.nome);
    setCusto(item.custo);
    setDataLimite(item.dataLimite);
  };

  const saveEdit = async (e) => {
    e.preventDefault(); // Impede o comportamento padrão do formulário

    // Verificar se o novo nome já existe
    const nameExists = items.some(
      (item) => item.nome === nome && item.id !== isEditing.id
    );

    if (nameExists) {
      alert("O nome da tarefa já existe. Por favor, escolha outro nome.");
      return;
    }

    const updatedItem = {
      nome,
      custo: parseFloat(custo),
      dataLimite,
    };

    try {
      await axios.put(
        `https://listadetarefas-2.onrender.com/tarefas/${isEditing.id}`,
        updatedItem
      );
      fetchItems(); // Recarrega a lista de itens após a edição
      setIsEditing(null); // Limpa o estado de edição
      setNome("");
      setCusto("");
      setDataLimite("");
    } catch (error) {
      console.error("Erro ao editar item", error);
    }
  };

  useEffect(() => {
    fetchItems();
  }, []);

  return (
    <div>
      <h1>Tabela de Items</h1>
      <form onSubmit={isEditing ? saveEdit : addItem}>
        <input
          type="text"
          placeholder="Nome"
          value={nome}
          onChange={(e) => setNome(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Custo"
          value={custo}
          onChange={(e) => setCusto(e.target.value)}
          required
        />
        <input
          type="date"
          placeholder="Data Limite"
          value={dataLimite}
          onChange={(e) => setDataLimite(e.target.value)}
          required
        />
        <button type="submit">{isEditing ? "Salvar" : "Adicionar Item"}</button>
      </form>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nome</th>
            <th>Custo</th>
            <th>Data Limite</th>
            <th>Ordem de Apresentação</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {items.map((item) => (
            <tr key={item.id} className={item.custo >= 1000 ? "highlight" : ""}>
              <td>{item.id}</td>
              <td>{item.nome}</td>
              <td>{item.custo}</td>
              <td>{item.dataLimite}</td>
              <td>{item.ordemApresentacao}</td>
              <td>
                <button onClick={() => startEditing(item)}>Editar</button>
                <button onClick={() => deleteItem(item.id)}>Excluir</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default TabelaItems;
