import React, { useState } from "react";

export const EditTodoForm = ({ editTodo, task }) => {
  const [nome, setNome] = useState(task.nome);
  const [custo, setCusto] = useState(task.custo);
  const [dataLimite, setDataLimite] = useState(task.dataLimite);

  const handleSubmit = (e) => {
    e.preventDefault();
    editTodo({ nome, custo: parseFloat(custo), dataLimite }, task.id); // Passa os campos corretos
  };

  return (
    <form onSubmit={handleSubmit} className="TodoForm">
      <input
        type="text"
        value={nome}
        onChange={(e) => setNome(e.target.value)}
        className="todo-input"
        placeholder="Nome"
      />
      <input
        type="number"
        value={custo}
        onChange={(e) => setCusto(e.target.value)}
        className="todo-input"
        placeholder="Custo"
      />
      <input
        type="date"
        value={dataLimite}
        onChange={(e) => setDataLimite(e.target.value)}
        className="todo-input"
        placeholder="Data Limite"
      />
      <button type="submit" className="todo-btn">
        Atualizar tarefa
      </button>
    </form>
  );
};
