import React, { useEffect, useState } from "react";
import axios from "axios";
import { format } from "date-fns";
import Modal from "./Modal";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import "./TabelaItems.css";

const TabelaItems = () => {
  const [items, setItems] = useState([]);
  const [nome, setNome] = useState("");
  const [custo, setCusto] = useState("");
  const [dataLimite, setDataLimite] = useState("");
  const [isEditing, setIsEditing] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const fetchItems = async () => {
    try {
      const response = await axios.get(
        "https://listadetarefas-2.onrender.com/tarefas"
      );
      setItems(response.data);
    } catch (error) {
      console.error("Erro ao buscar os itens", error);
    }
  };

  const addItem = async (e) => {
    e.preventDefault();
    const ordemApresentacao =
      items.length > 0
        ? Math.max(...items.map((item) => item.ordemApresentacao)) + 1
        : 1;
    const newItem = {
      nome,
      custo: parseFloat(custo),
      dataLimite,
      ordemApresentacao,
    };
    try {
      await axios.post(
        "https://listadetarefas-2.onrender.com/tarefas",
        newItem
      );
      fetchItems();
      setNome("");
      setCusto("");
      setDataLimite("");
    } catch (error) {
      console.error(
        "Erro ao adicionar item",
        error.response ? error.response.data : error.message
      );
    }
  };

  const deleteItem = async (id) => {
    if (window.confirm("Você tem certeza que deseja excluir este item?")) {
      try {
        await axios.delete(
          `https://listadetarefas-2.onrender.com/tarefas/${id}`
        );
        fetchItems();
      } catch (error) {
        console.error("Erro ao excluir o item", error);
      }
    }
  };

  const startEditing = (item) => {
    setIsEditing(item);
    setNome(item.nome);
    setCusto(item.custo);
    setDataLimite(item.dataLimite);
    setIsModalOpen(true);
  };

  const saveEdit = async (e) => {
    e.preventDefault();
    const nameExists = items.some(
      (item) => item.nome === nome && item.id !== isEditing.id
    );
    if (nameExists) {
      alert("O nome da tarefa já existe. Por favor, escolha outro nome.");
      return;
    }
    const updatedItem = { nome, custo: parseFloat(custo), dataLimite };
    try {
      await axios.put(
        `https://listadetarefas-2.onrender.com/tarefas/${isEditing.id}`,
        updatedItem
      );
      fetchItems();
      setIsEditing(null);
      setIsModalOpen(false);
      setNome("");
      setCusto("");
      setDataLimite("");
    } catch (error) {
      console.error("Erro ao editar item", error);
    }
  };

  const handleOnDragEnd = (result) => {
    if (!result.destination) return;

    const itemsCopy = Array.from(items);
    const [removed] = itemsCopy.splice(result.source.index, 1);
    itemsCopy.splice(result.destination.index, 0, removed);

    // Atualiza a ordem de apresentação com base na nova posição
    const updatedItems = itemsCopy.map((item, index) => ({
      ...item,
      ordemApresentacao: index + 1, // Atualiza a ordem conforme a posição na tabela
    }));

    setItems(updatedItems);
  };

  useEffect(() => {
    fetchItems();
  }, []);

  return (
    <div className="container">
      <h1>Tabela de Items</h1>
      <form onSubmit={addItem}>
        <input
          type="text"
          placeholder="Nome"
          value={nome}
          onChange={(e) => setNome(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Custo"
          value={custo}
          onChange={(e) => setCusto(e.target.value)}
          required
        />
        <input
          type="date"
          placeholder="Data Limite"
          value={dataLimite}
          onChange={(e) => setDataLimite(e.target.value)}
          required
        />
        <button type="submit">Adicionar Item</button>
      </form>

      <DragDropContext onDragEnd={handleOnDragEnd}>
        <Droppable droppableId="droppable">
          {" "}
          {/* Certifique-se de que o droppableId é único e constante */}
          {(provided) => (
            <table ref={provided.innerRef} {...provided.droppableProps}>
              {" "}
              {/* Aplicar innerRef e droppableProps diretamente na tabela */}
              <thead>
                <tr>
                  <th>Arraste</th>
                  <th>ID</th>
                  <th>Nome</th>
                  <th>Custo</th>
                  <th>Data Limite</th>
                  <th>Ordem de Apresentação</th>{" "}
                  {/* A coluna de Ordem de Apresentação agora está visível */}
                  <th>Ações</th>
                </tr>
              </thead>
              <tbody>
                {items
                  .sort((a, b) => a.ordemApresentacao - b.ordemApresentacao) // Ordena os itens pela ordem de apresentação
                  .map((item, index) => (
                    <Draggable
                      key={item.id}
                      draggableId={item.id.toString()}
                      index={index}
                    >
                      {(provided) => (
                        <tr
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          className={item.custo >= 1000 ? "highlight" : ""}
                        >
                          <td
                            {...provided.dragHandleProps}
                            style={{ cursor: "grab" }}
                          >
                            <span>☰</span>
                          </td>
                          <td>{item.id}</td>
                          <td>{item.nome}</td>
                          <td>{item.custo}</td>
                          <td>
                            {format(new Date(item.dataLimite), "dd-MM-yyyy")}
                          </td>
                          <td
                            {...provided.dragHandleProps}
                            style={{ cursor: "grab" }}
                          >
                            {" "}
                            {/* Torna a célula da ordem de apresentação arrastável */}
                            {item.ordemApresentacao}
                          </td>
                          <td>
                            <button onClick={() => startEditing(item)}>
                              Editar
                            </button>
                            <button onClick={() => deleteItem(item.id)}>
                              Excluir
                            </button>
                          </td>
                        </tr>
                      )}
                    </Draggable>
                  ))}
                {provided.placeholder}{" "}
                {/* O placeholder é importante para o layout */}
              </tbody>
            </table>
          )}
        </Droppable>
      </DragDropContext>

      <Modal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)}>
        <h2>Editar Item</h2>
        <form onSubmit={saveEdit}>
          <input
            type="text"
            value={nome}
            onChange={(e) => setNome(e.target.value)}
            required
            placeholder="Nome"
          />
          <input
            type="number"
            value={custo}
            onChange={(e) => setCusto(e.target.value)}
            required
            placeholder="Custo"
          />
          <input
            type="date"
            value={dataLimite}
            onChange={(e) => setDataLimite(e.target.value)}
            required
            placeholder="Data Limite"
          />
          <button type="submit">Salvar</button>
        </form>
      </Modal>
    </div>
  );
};

export default TabelaItems;
