import React, { useEffect, useState } from "react";
import axios from "axios";
import { format } from "date-fns"; // Importando a função format
import { EditTodoForm } from "./EditTodoForm"; // Certifique-se de importar o EditTodoForm

const TabelaItems = () => {
  const [items, setItems] = useState([]);
  const [nome, setNome] = useState("");
  const [custo, setCusto] = useState("");
  const [dataLimite, setDataLimite] = useState("");
  const [editingItem, setEditingItem] = useState(null); // Estado para o item que está sendo editado

  const fetchItems = async () => {
    try {
      const response = await axios.get(
        "https://listadetarefas-2.onrender.com/tarefas"
      );
      setItems(response.data);
    } catch (error) {
      console.error("Erro ao buscar os itens", error);
    }
  };

  const addItem = async (e) => {
    e.preventDefault(); // Evita o comportamento padrão do formulário

    const ordemApresentacao =
      items.length > 0
        ? Math.max(...items.map((item) => item.ordemApresentacao)) + 1
        : 1;

    // Formata a dataLimite para o formato dd-MM-yyyy
    const formattedDataLimite = format(new Date(dataLimite), "dd-MM-yyyy");

    const newItem = {
      nome,
      custo: parseFloat(custo),
      dataLimite: formattedDataLimite,
      ordemApresentacao,
    };

    try {
      const response = await axios.post(
        "https://listadetarefas-2.onrender.com/tarefas",
        newItem
      );
      console.log("Resposta da API:", response.data); // Log da resposta
      fetchItems();
      resetForm();
    } catch (error) {
      console.error(
        "Erro ao adicionar item",
        error.response ? error.response.data : error.message
      );
    }
  };

  const deleteItem = async (id) => {
    try {
      await axios.delete(`https://listadetarefas-2.onrender.com/tarefas/${id}`);
      fetchItems(); // Atualiza a lista após a exclusão
    } catch (error) {
      console.error(
        "Erro ao deletar item",
        error.response ? error.response.data : error.message
      );
    }
  };

  const editItem = (item) => {
    setEditingItem(item); // Define o item que está sendo editado
    setNome(item.nome);
    setCusto(item.custo);
    setDataLimite(item.dataLimite);
  };

  const updateItem = async (updatedItem, id) => {
    try {
      await axios.put(
        `https://listadetarefas-2.onrender.com/tarefas/${id}`,
        updatedItem
      );
      fetchItems();
      resetForm();
      setEditingItem(null); // Reseta o item sendo editado
    } catch (error) {
      console.error(
        "Erro ao atualizar item",
        error.response ? error.response.data : error.message
      );
    }
  };

  const resetForm = () => {
    setNome("");
    setCusto("");
    setDataLimite("");
  };

  useEffect(() => {
    fetchItems();
  }, []);

  return (
    <div>
      <h1>Tabela de Items</h1>
      <form
        onSubmit={
          editingItem
            ? (e) => {
                e.preventDefault();
                updateItem(
                  { nome, custo: parseFloat(custo), dataLimite },
                  editingItem.id
                );
              }
            : addItem
        }
      >
        <input
          type="text"
          placeholder="Nome"
          value={nome}
          onChange={(e) => setNome(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Custo"
          value={custo}
          onChange={(e) => setCusto(e.target.value)}
          required
        />
        <input
          type="date"
          placeholder="Data Limite"
          value={dataLimite}
          onChange={(e) => setDataLimite(e.target.value)}
          required
        />
        <button type="submit">
          {editingItem ? "Atualizar Item" : "Adicionar Item"}
        </button>
      </form>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nome</th>
            <th>Custo</th>
            <th>Data Limite</th>
            <th>Ordem de Apresentação</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {items
            .sort((a, b) => b.ordemApresentacao - a.ordemApresentacao)
            .map((item) => (
              <tr key={item.id}>
                <td>{item.id}</td>
                <td>{item.nome}</td>
                <td>{item.custo}</td>
                <td>{item.dataLimite}</td>
                <td>{item.ordemApresentacao}</td>
                <td>
                  <button onClick={() => editItem(item)}>Editar</button>
                  <button onClick={() => deleteItem(item.id)}>Deletar</button>
                </td>
              </tr>
            ))}
        </tbody>
      </table>
      {editingItem && <EditTodoForm editTodo={updateItem} task={editingItem} />}
    </div>
  );
};

export default TabelaItems;
