import React, { useEffect, useState } from "react";
import axios from "axios";
import { format } from "date-fns"; // Importando a função format

const TabelaItems = () => {
  const [items, setItems] = useState([]);
  const [nome, setNome] = useState("");
  const [custo, setCusto] = useState("");
  const [dataLimite, setDataLimite] = useState("");
  const [editId, setEditId] = useState(null); // Estado para controle de edição

  const fetchItems = async () => {
    try {
      const response = await axios.get(
        "https://listadetarefas-2.onrender.com/tarefas"
      );
      setItems(response.data);
    } catch (error) {
      console.error("Erro ao buscar os itens", error);
    }
  };

  const addItem = async (e) => {
    e.preventDefault(); // Adicione isso para evitar o comportamento padrão do formulário

    const ordemApresentacao =
      items.length > 0
        ? Math.max(...items.map((item) => item.ordemApresentacao)) -
          items.length
        : 0; // Ou 0 se não houver itens

    // Formata a dataLimite para o formato dd-MM-yyyy
    const formattedDataLimite = format(new Date(dataLimite), "dd-MM-yyyy");

    const newItem = {
      nome,
      custo: parseFloat(custo),
      dataLimite: formattedDataLimite,
      ordemApresentacao,
    };

    console.log("Novo Item:", newItem); // Log para verificar o item que será enviado

    try {
      const response = await axios.post(
        "https://listadetarefas-2.onrender.com/tarefas",
        newItem
      );
      console.log("Resposta da API:", response.data); // Log da resposta
      fetchItems();
      setNome("");
      setCusto("");
      setDataLimite("");
    } catch (error) {
      console.error(
        "Erro ao adicionar item",
        error.response ? error.response.data : error.message
      );
    }
  };

  const deleteItem = async (id) => {
    try {
      await axios.delete(`https://listadetarefas-2.onrender.com/tarefas/${id}`);
      fetchItems(); // Atualiza a lista de itens após a exclusão
    } catch (error) {
      console.error("Erro ao deletar item", error);
    }
  };

  const editItem = (item) => {
    setNome(item.nome);
    setCusto(item.custo);
    setDataLimite(item.dataLimite);
    setEditId(item.id); // Armazena o ID do item que está sendo editado
  };

  const updateItem = async (e) => {
    e.preventDefault(); // Para evitar o comportamento padrão do formulário

    const ordemApresentacao =
      items.length > 0
        ? Math.max(...items.map((item) => item.ordemApresentacao)) -
          items.length
        : 0; // Ou 0 se não houver itens

    const formattedDataLimite = format(new Date(dataLimite), "dd-MM-yyyy");

    const updatedItem = {
      nome,
      custo: parseFloat(custo),
      dataLimite: formattedDataLimite,
      ordemApresentacao,
    };

    try {
      await axios.put(
        `https://listadetarefas-2.onrender.com/tarefas/${editId}`,
        updatedItem
      );
      fetchItems();
      setNome("");
      setCusto("");
      setDataLimite("");
      setEditId(null); // Reseta o ID após a edição
    } catch (error) {
      console.error("Erro ao atualizar item", error);
    }
  };

  useEffect(() => {
    fetchItems();
  }, []);

  return (
    <div>
      <h1>Tabela de Items</h1>
      <form onSubmit={editId ? updateItem : addItem}>
        <input
          type="text"
          placeholder="Nome"
          value={nome}
          onChange={(e) => setNome(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Custo"
          value={custo}
          onChange={(e) => setCusto(e.target.value)}
          required
        />
        <input
          type="date"
          placeholder="Data Limite"
          value={dataLimite}
          onChange={(e) => setDataLimite(e.target.value)}
          required
        />
        <button type="submit">
          {editId ? "Atualizar Item" : "Adicionar Item"}
        </button>
      </form>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nome</th>
            <th>Custo</th>
            <th>Data Limite</th>
            <th>Ordem de Apresentação</th>
            <th>Ações</th> {/* Coluna de Ações */}
          </tr>
        </thead>
        <tbody>
          {items.map((item) => (
            <tr key={item.id}>
              <td>{item.id}</td>
              <td>{item.nome}</td>
              <td>{item.custo}</td>
              <td>{item.dataLimite}</td>
              <td>{item.ordemApresentacao}</td>
              <td>
                <button onClick={() => editItem(item)}>Editar</button>
                <button onClick={() => deleteItem(item.id)}>Deletar</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default TabelaItems;
