import React, { useEffect, useState } from "react";
import axios from "axios";
import { format } from "date-fns"; // Importando a função format
import Modal from "./Modal"; // Importa o Modal
import "./TabelaItems.css"; // Importe seu arquivo CSS

const TabelaItems = () => {
  const [items, setItems] = useState([]);
  const [nome, setNome] = useState("");
  const [custo, setCusto] = useState("");
  const [dataLimite, setDataLimite] = useState("");
  const [isEditing, setIsEditing] = useState(null); // Estado para gerenciar o item que está sendo editado
  const [isModalOpen, setIsModalOpen] = useState(false); // Estado para controlar a abertura do modal

  const fetchItems = async () => {
    try {
      const response = await axios.get(
        "https://listadetarefas-2.onrender.com/tarefas"
      );
      setItems(response.data);
    } catch (error) {
      console.error("Erro ao buscar os itens", error);
    }
  };

  const addItem = async (e) => {
    e.preventDefault();

    const ordemApresentacao =
      items.length > 0
        ? Math.max(...items.map((item) => item.ordemApresentacao)) + 1
        : 1;

    const newItem = {
      nome,
      custo: parseFloat(custo),
      dataLimite, // Usa a data diretamente do input
      ordemApresentacao,
    };

    try {
      await axios.post(
        "https://listadetarefas-2.onrender.com/tarefas",
        newItem
      );
      fetchItems();
      setNome("");
      setCusto("");
      setDataLimite("");
    } catch (error) {
      console.error(
        "Erro ao adicionar item",
        error.response ? error.response.data : error.message
      );
    }
  };

  const deleteItem = async (id) => {
    const confirmDelete = window.confirm(
      "Você tem certeza que deseja excluir este item?"
    );
    if (confirmDelete) {
      try {
        await axios.delete(
          `https://listadetarefas-2.onrender.com/tarefas/${id}`
        );
        fetchItems(); // Recarrega os itens após a exclusão
      } catch (error) {
        console.error("Erro ao excluir o item", error);
      }
    }
  };

  const startEditing = (item) => {
    setIsEditing(item);
    setNome(item.nome);
    setCusto(item.custo);
    setDataLimite(item.dataLimite); // Usa a data diretamente do item
    setIsModalOpen(true); // Abre o modal ao iniciar a edição
  };

  const saveEdit = async (e) => {
    e.preventDefault();

    const nameExists = items.some(
      (item) => item.nome === nome && item.id !== isEditing.id
    );

    if (nameExists) {
      alert("O nome da tarefa já existe. Por favor, escolha outro nome.");
      return;
    }

    const updatedItem = {
      nome,
      custo: parseFloat(custo),
      dataLimite, // Usa a data diretamente do input
      ordemApresentacao: isEditing.ordemApresentacao, // Mantém a ordem de apresentação
    };

    try {
      await axios.put(
        `https://listadetarefas-2.onrender.com/tarefas/${isEditing.id}`,
        updatedItem
      );
      fetchItems();
      setIsEditing(null);
      setIsModalOpen(false);
      setNome("");
      setCusto("");
      setDataLimite("");
    } catch (error) {
      console.error("Erro ao editar item", error);
    }
  };

  const moveItem = async (id, direction) => {
    const itemIndex = items.findIndex((item) => item.id === id);
    if (direction === "up" && itemIndex > 0) {
      // Mover para cima
      const newItems = [...items];
      [newItems[itemIndex], newItems[itemIndex - 1]] = [
        newItems[itemIndex - 1],
        newItems[itemIndex],
      ];
      await updateItems(newItems);
    } else if (direction === "down" && itemIndex < items.length - 1) {
      // Mover para baixo
      const newItems = [...items];
      [newItems[itemIndex], newItems[itemIndex + 1]] = [
        newItems[itemIndex + 1],
        newItems[itemIndex],
      ];
      await updateItems(newItems);
    }
  };

  const updateItems = async (newItems) => {
    // Adiciona a classe para animação
    setItems(
      newItems.map((item, index) => ({
        ...item,
        ordemApresentacao: index + 1,
      }))
    );

    // Atualiza os dados no servidor
    for (const item of newItems) {
      await axios.put(
        `https://listadetarefas-2.onrender.com/tarefas/${item.id}`,
        item
      );
    }
  };

  return (
    <div className="container">
      <h1>Tabela de Items</h1>
      <form onSubmit={addItem}>
        <input
          type="text"
          placeholder="Nome"
          value={nome}
          onChange={(e) => setNome(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Custo"
          value={custo}
          onChange={(e) => setCusto(e.target.value)}
          required
        />
        <input
          type="date"
          placeholder="Data Limite"
          value={dataLimite}
          onChange={(e) => setDataLimite(e.target.value)}
          required
        />
        <button type="submit">Adicionar Item</button>
      </form>

      <table>
        <thead>
          <tr>
            <th>Ações</th>
            <th>ID</th>
            <th>Nome</th>
            <th>Custo</th>
            <th>Data Limite</th>
            <th>Ordem de Apresentação</th>
          </tr>
        </thead>
        <tbody>
          {items.map((item, index) => (
            <tr
              key={item.id}
              className={`table-row ${item.moving ? "up" : ""} ${
                item.movingDown ? "down" : ""
              }`}
            >
              <td>
                <button
                  onClick={() => moveItem(item.id, "up")}
                  disabled={index === 0}
                >
                  ↑
                </button>
                <button
                  onClick={() => moveItem(item.id, "down")}
                  disabled={index === items.length - 1}
                >
                  ↓
                </button>
              </td>
              <td>{item.id}</td>
              <td>{item.nome}</td>
              <td>{item.custo}</td>
              <td>{format(new Date(item.dataLimite), "dd-MM-yyyy")}</td>
              <td>{item.ordemApresentacao}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <Modal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)}>
        <h2>Editar Item</h2>
        <form onSubmit={saveEdit}>
          <input
            type="text"
            value={nome}
            onChange={(e) => setNome(e.target.value)}
            required
            placeholder="Nome"
          />
          <input
            type="number"
            value={custo}
            onChange={(e) => setCusto(e.target.value)}
            required
            placeholder="Custo"
          />
          <input
            type="date"
            value={dataLimite}
            onChange={(e) => setDataLimite(e.target.value)}
            required
            placeholder="Data Limite"
          />
          <button type="submit">Salvar</button>
        </form>
      </Modal>
    </div>
  );
};

export default TabelaItems;
