import React, { useState, useEffect } from "react";
import { Todo } from "./Todo";
import { TodoForm } from "./TodoForm";
import { EditTodoForm } from "./EditTodoForm";
import axios from "axios"; // Importando o Axios

export const TodoWrapper = () => {
  const [todos, setTodos] = useState([]);

  // Função para buscar tarefas da API
  const fetchTodos = async () => {
    try {
      const response = await axios.get(
        "https://listadetarefas-2.onrender.com/tarefas"
      );
      setTodos(response.data);
    } catch (error) {
      console.error("Erro ao buscar tarefas:", error);
    }
  };

  // Efeito colateral para buscar tarefas ao montar o componente
  useEffect(() => {
    fetchTodos();
  }, []);

  const addTodo = async (todo) => {
    const newItem = {
      nome: todo,
      custo: 0, // Defina conforme necessário
      dataLimite: "01-01-2025", // Defina um valor padrão se necessário
      ordemApresentacao: todos.length + 1, // Lógica para ordem
    };

    try {
      await axios.post(
        "https://listadetarefas-2.onrender.com/tarefas",
        newItem
      );
      fetchTodos(); // Recarregue os dados após adicionar
    } catch (error) {
      console.error("Erro ao adicionar tarefa:", error);
    }
  };

  const deleteTodo = async (id) => {
    try {
      await axios.delete(`https://listadetarefas-2.onrender.com/tarefas/${id}`);
      fetchTodos(); // Recarregue os dados após deletar
    } catch (error) {
      console.error("Erro ao deletar tarefa:", error);
    }
  };

  const toggleComplete = (id) => {
    console.log(`Tarefa com ID ${id} completada`); // Implementar se necessário
  };

  const editTodo = (id) => {
    setTodos(
      todos.map((todo) =>
        todo.id === id ? { ...todo, isEditing: !todo.isEditing } : todo
      )
    );
  };

  const editTask = async (task, id) => {
    try {
      await axios.put(`https://listadetarefas-2.onrender.com/tarefas/${id}`, {
        nome: task,
      });
      fetchTodos(); // Recarregue os dados após a edição
    } catch (error) {
      console.error("Erro ao editar tarefa:", error);
    }
  };

  return (
    <div className="TodoWrapper">
      <h1>Lista de Tarefas</h1>
      <TodoForm addTodo={addTodo} />
      {todos.map((todo) =>
        todo.isEditing ? (
          <EditTodoForm key={todo.id} editTodo={editTask} task={todo} />
        ) : (
          <Todo
            key={todo.id}
            task={todo}
            deleteTodo={deleteTodo}
            editTodo={editTodo}
            toggleComplete={toggleComplete}
          />
        )
      )}
    </div>
  );
};
