import React, { useState, useEffect } from "react";

export const EditTodoForm = ({ task, editTodo }) => {
  const [nome, setNome] = useState(task.nome);
  const [custo, setCusto] = useState(task.custo);
  const [dataLimite, setDataLimite] = useState(task.dataLimite);
  const [ordemApresentacao, setOrdemApresentacao] = useState(
    task.ordemApresentacao
  );

  useEffect(() => {
    setNome(task.nome);
    setCusto(task.custo);
    setDataLimite(task.dataLimite);
    setOrdemApresentacao(task.ordemApresentacao);
  }, [task]);

  const handleSubmit = (e) => {
    e.preventDefault();
    editTodo({ nome, custo, dataLimite, ordemApresentacao }, task.id);
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        value={nome}
        onChange={(e) => setNome(e.target.value)}
        required
      />
      <input
        type="number"
        value={custo}
        onChange={(e) => setCusto(parseFloat(e.target.value))}
        required
      />
      <input
        type="date"
        value={dataLimite}
        onChange={(e) => setDataLimite(e.target.value)}
        required
      />
      <input
        type="number"
        value={ordemApresentacao}
        onChange={(e) => setOrdemApresentacao(parseInt(e.target.value))}
        required
      />
      <button type="submit">Salvar</button>
    </form>
  );
};
