import React, { useEffect, useState } from "react";
import axios from "axios";
import { format } from "date-fns";
import { EditTodoForm } from "./EditTodoForm"; // Importe o componente EditTodoForm

const TabelaItems = () => {
  const [items, setItems] = useState([]);
  const [nome, setNome] = useState("");
  const [custo, setCusto] = useState("");
  const [dataLimite, setDataLimite] = useState("");
  const [editingTask, setEditingTask] = useState(null); // Adicione este estado para gerenciar a tarefa em edição

  const fetchItems = async () => {
    try {
      const response = await axios.get(
        "https://listadetarefas-2.onrender.com/tarefas"
      );
      setItems(response.data);
    } catch (error) {
      console.error("Erro ao buscar os itens", error);
    }
  };

  const addItem = async (e) => {
    e.preventDefault();

    const ordemApresentacao =
      items.length > 0
        ? Math.max(...items.map((item) => item.ordemApresentacao)) + 1
        : 1;

    // Formata a dataLimite para o formato dd-MM-yyyy
    const formattedDataLimite = format(new Date(dataLimite), "dd-MM-yyyy");

    const newItem = {
      nome,
      custo: parseFloat(custo),
      dataLimite: formattedDataLimite,
      ordemApresentacao,
    };

    try {
      await axios.post(
        "https://listadetarefas-2.onrender.com/tarefas",
        newItem
      );
      fetchItems();
      setNome("");
      setCusto("");
      setDataLimite("");
    } catch (error) {
      console.error(
        "Erro ao adicionar item",
        error.response ? error.response.data : error.message
      );
    }
  };

  const editTodo = async (newValue, id) => {
    try {
      // Realiza a requisição para editar a tarefa
      const response = await axios.put(
        `https://listadetarefas-2.onrender.com/tarefas/${id}`,
        {
          nome: newValue,
          // Adicione outros campos que precisam ser atualizados
        }
      );
      console.log("Tarefa atualizada:", response.data);
      fetchItems(); // Atualiza a lista de itens após a edição
      setEditingTask(null); // Limpa o estado de edição após a atualização
    } catch (error) {
      console.error(
        "Erro ao atualizar a tarefa",
        error.response ? error.response.data : error.message
      );
    }
  };

  useEffect(() => {
    fetchItems();
  }, []);

  return (
    <div>
      <h1>Tabela de Items</h1>
      <form onSubmit={addItem}>
        <input
          type="text"
          placeholder="Nome"
          value={nome}
          onChange={(e) => setNome(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Custo"
          value={custo}
          onChange={(e) => setCusto(e.target.value)}
          required
        />
        <input
          type="date"
          placeholder="Data Limite"
          value={dataLimite}
          onChange={(e) => setDataLimite(e.target.value)}
          required
        />
        <button type="submit">Adicionar Item</button>
      </form>

      {/* Se uma tarefa estiver sendo editada, mostra o formulário de edição */}
      {editingTask && <EditTodoForm editTodo={editTodo} task={editingTask} />}

      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nome</th>
            <th>Custo</th>
            <th>Data Limite</th>
            <th>Ordem de Apresentação</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {items.map((item) => (
            <tr key={item.id}>
              <td>{item.id}</td>
              <td>{item.nome}</td>
              <td>{item.custo}</td>
              <td>{item.dataLimite}</td>
              <td>{item.ordemApresentacao}</td>
              <td>
                <button onClick={() => setEditingTask(item)}>Editar</button>{" "}
                {/* Botão para editar a tarefa */}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default TabelaItems;
