import React, { useEffect, useState } from "react";
import axios from "axios";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd"; // Importe as funcionalidades do DnD
import "./TabelaItems.css"; // Importe seu arquivo CSS
import Modal from "./Modal"; // Importe o Modal

const TabelaItems = () => {
  const [items, setItems] = useState([]);
  const [nome, setNome] = useState("");
  const [custo, setCusto] = useState("");
  const [dataLimite, setDataLimite] = useState("");
  const [isEditing, setIsEditing] = useState(null); // Estado para gerenciar o item que está sendo editado
  const [isModalOpen, setIsModalOpen] = useState(false); // Estado para controlar a abertura do modal

  const fetchItems = async () => {
    try {
      const response = await axios.get(
        "https://listadetarefas-2.onrender.com/tarefas"
      );
      setItems(response.data);
    } catch (error) {
      console.error("Erro ao buscar os itens", error);
    }
  };

  const addItem = async (e) => {
    e.preventDefault();

    const newItem = {
      nome,
      custo: parseFloat(custo),
      dataLimite, // Assume que já está no formato correto
    };

    try {
      await axios.post(
        "https://listadetarefas-2.onrender.com/tarefas",
        newItem
      );
      fetchItems(); // Recarrega a lista de itens após a adição
      setNome("");
      setCusto("");
      setDataLimite("");
    } catch (error) {
      console.error("Erro ao adicionar item", error);
    }
  };

  const deleteItem = async (id) => {
    const confirmDelete = window.confirm(
      "Você tem certeza que deseja excluir este item?"
    );
    if (confirmDelete) {
      try {
        await axios.delete(
          `https://listadetarefas-2.onrender.com/tarefas/${id}`
        );
        fetchItems(); // Recarrega a lista de itens após a exclusão
      } catch (error) {
        console.error("Erro ao excluir o item", error);
      }
    }
  };

  const startEditing = (item) => {
    setIsEditing(item); // Define o item que está sendo editado
    setNome(item.nome);
    setCusto(item.custo);
    setDataLimite(item.dataLimite);
    setIsModalOpen(true); // Abre o modal ao iniciar a edição
  };

  const saveEdit = async (e) => {
    e.preventDefault();

    const updatedItem = {
      nome,
      custo: parseFloat(custo),
      dataLimite,
    };

    try {
      await axios.put(
        `https://listadetarefas-2.onrender.com/tarefas/${isEditing.id}`,
        updatedItem
      );
      fetchItems(); // Recarrega a lista de itens após a edição
      setIsEditing(null); // Limpa o estado de edição
      setIsModalOpen(false); // Fecha o modal
      setNome("");
      setCusto("");
      setDataLimite("");
    } catch (error) {
      console.error("Erro ao editar item", error);
    }
  };

  // Função para lidar com o final do arrastar e soltar
  const onDragEnd = async (result) => {
    if (!result.destination) return; // Se não houver destino, não faz nada

    const reorderedItems = Array.from(items); // Faz uma cópia do array de itens
    const [movedItem] = reorderedItems.splice(result.source.index, 1); // Remove o item da posição original
    reorderedItems.splice(result.destination.index, 0, movedItem); // Adiciona o item na nova posição

    setItems(reorderedItems); // Atualiza o estado com a nova ordem

    // Aqui você pode fazer uma requisição para atualizar a ordem no backend, se necessário
    // Exemplo:
    // await axios.put(`https://your-api-url/items/reorder`, { reorderedItems });
  };

  useEffect(() => {
    fetchItems();
  }, []);

  return (
    <div>
      <h1>Tabela de Items</h1>
      <form onSubmit={addItem}>
        <input
          type="text"
          placeholder="Nome"
          value={nome}
          onChange={(e) => setNome(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Custo"
          value={custo}
          onChange={(e) => setCusto(e.target.value)}
          required
        />
        <input
          type="date"
          placeholder="Data Limite"
          value={dataLimite}
          onChange={(e) => setDataLimite(e.target.value)}
          required
        />
        <button type="submit">Adicionar Item</button>
      </form>

      <DragDropContext onDragEnd={onDragEnd}>
        <Droppable droppableId="items">
          {(provided) => (
            <table ref={provided.innerRef} {...provided.droppableProps}>
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Nome</th>
                  <th>Custo</th>
                  <th>Data Limite</th>
                  <th>Ações</th>
                </tr>
              </thead>
              <tbody>
                {items.map((item, index) => (
                  <Draggable
                    key={item.id}
                    draggableId={String(item.id)}
                    index={index}
                  >
                    {(provided) => (
                      <tr
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                      >
                        <td>{item.id}</td>
                        <td>{item.nome}</td>
                        <td>{item.custo}</td>
                        <td>{item.dataLimite}</td>
                        <td>
                          <button onClick={() => startEditing(item)}>
                            Editar
                          </button>
                          <button onClick={() => deleteItem(item.id)}>
                            Excluir
                          </button>
                        </td>
                      </tr>
                    )}
                  </Draggable>
                ))}
              </tbody>
            </table>
          )}
        </Droppable>
      </DragDropContext>

      <Modal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)}>
        <h2>Editar Item</h2>
        <form onSubmit={saveEdit}>
          <input
            type="text"
            value={nome}
            onChange={(e) => setNome(e.target.value)}
            required
            placeholder="Nome"
          />
          <input
            type="number"
            value={custo}
            onChange={(e) => setCusto(e.target.value)}
            required
            placeholder="Custo"
          />
          <input
            type="date"
            value={dataLimite}
            onChange={(e) => setDataLimite(e.target.value)}
            required
            placeholder="Data Limite"
          />
          <button type="submit">Salvar</button>
        </form>
      </Modal>
    </div>
  );
};

export default TabelaItems;
