import React from "react";

export const Todo = ({ task, deleteTodo, editTodo, toggleComplete }) => {
  return (
    <div className={`todo ${task.completed ? "completed" : ""}`}>
      <h3>{task.nome}</h3>
      <p>Custo: {task.custo.toFixed(2)} R$</p>
      <p>Data Limite: {task.dataLimite}</p>
      <p>Ordem de Apresentação: {task.ordemApresentacao}</p>

      <button onClick={() => toggleComplete(task.id)}>
        {task.completed ? "Desmarcar" : "Marcar como Concluída"}
      </button>
      <button onClick={() => editTodo(task.id)}>Editar</button>
      <button onClick={() => deleteTodo(task.id)}>Deletar</button>
    </div>
  );
};
